import React, {useState} from 'react'

export default function Textbox(props) {
    // let obj={
    //     outerHeight: 50,
    //     backgroundColor: 'Red'
    // }
    const handleClickcl = ()=>{
        setText("");
    }
    const handleClickup = ()=>{
        console.log("UPPERCASE BUTTON CLICKED")
        let newText=text.toUpperCase();
        setText(newText);
    }
    const handleClicklw = ()=>{
        console.log("LOWERCASE BUTTON CLICKED")
        let newText=text.toLowerCase();
        setText(newText);
    }
    const handleOnChange = (event)=>{
        console.log("CHANGE HANDLED");
        setText(event.target.value);
    }
    const [text,setText] = useState("");
  return (
    <>
    <div className="form-floating" rows="10">
        <h1 style={{color : props.mode === 'light'?'black':'white'}}>{props.title}</h1>
    <textarea className="form-control" value={text} id="floatingTextarea2" onChange={handleOnChange} style={{height: '200px',backgroundColor : props.mode === 'light'?'black':'white'}}></textarea>
    <div>
        <button className="btn btn-primary my-3 MX-3" onClick={handleClickup}>Convert To UpperCase</button>
        <button className="btn btn-primary my-3 mx-3" onClick={handleClicklw}>Convert To LowerCase</button>
        <button className="btn btn-primary my-3 " onClick={handleClickcl}>Clear</button>
    </div>
    </div>
    <div className="container my-3">
        <h1>YOU TEXT SUMMARY</h1>
        <p>WORDS = <b>{text.split(" ").length} words</b> CHARACTERS = <b>{text.length} characters</b> </p>
        <h2>PREVIEW</h2>
        <p>{text}</p>
    </div>
    </>
  )
}
