import React, {useState} from 'react'

export default function Textbox(props) {
    const handleClickup = ()=>{
        console.log("UPPERCASE BUTTON CLICKED")
        let newText=text.toUpperCase();
        setText(newText);
    }
    const handleClicklw = ()=>{
        console.log("LOWERCASE BUTTON CLICKED")
        let newText=text.toLowerCase();
        setText(newText);
    }
    const handleOnChange = (event)=>{
        console.log("CHANGE HANDLED");
        setText(event.target.value);
    }
    const [text,setText] = useState('ENTER YOUR TEXT');
  return (
    <>
    <div className="form-floating" rows="10">
        <h1>{props.title}</h1>
    <textarea className="form-control" value={text} id="floatingTextarea2" onChange={handleOnChange} ></textarea>
    <div>
        <button className="btn btn-primary my-3" onClick={handleClickup}>Convert To UpperCase</button>
        <button className="btn btn-primary my-3 mx-3" onClick={handleClicklw}>Convert To LowerCase</button>
    </div>
    </div>
    <div className="container my-3">
        <h1>YOU TEXT SUMMARY</h1>
        <p>WORDS = {text.split(" ").length} words CHARACTERS = {text.length} characters </p>
    </div>
    </>
  )
}
