import React, {useState} from 'react'

export default function Textbox(props) {
    const handleClick = ()=>{
        console.log("BUTTON CLICKED")
        let newText=text.toUpperCase();
        setText(newText);
    }
    const handleOnChange = (event)=>{
        console.log("CHANGE HANDLED");
        setText(event.target.value);
    }
    const [text,setText] = useState('ENTER YOUR TEXT');
  return (
    <div className="form-floating" rows="10">
        <h1>{props.title}</h1>
    <textarea className="form-control" value={text} id="floatingTextarea2" onChange={handleOnChange} ></textarea>
    <div>
        <button className="btn btn-primary my-3" onClick={handleClick}>Convert To UpperCase</button>
    </div>
    </div>
  )
}
